mapper.select:
SELECT question_i_d,title,summary,cover,`type`,solution_num,image_description,`date`,statu,reason,account,tag
FROM `question`
WHERE 1=1
AND question_i_d=#{questionID}
AND title=#{title}
AND summary=#{summary}
AND cover=#{cover}
AND `type`=#{type}
AND solution_num=#{solutionNum}
AND image_description=#{imageDescription}
AND `date`=#{date}
AND statu=#{statu}
AND reason=#{reason}
AND account=#{account}
AND tag=#{tag}

SELECT title
FROM `question`
WHERE title like #{keyWork} OR summary like #{keyWork} LIMIT 0,#{suggestNum}

mapper.delete:
DELETE FROM `question`
WHERE 1=1
AND question_i_d=#{questionID}
AND title=#{title}
AND summary=#{summary}
AND cover=#{cover}
AND `type`=#{type}
AND solution_num=#{solutionNum}
AND image_description=#{imageDescription}
AND `date`=#{date}
AND statu=#{statu}
AND reason=#{reason}
AND account=#{account}
AND tag=#{tag}

mapper.update:
UPDATE `question`
WHERE 1=1 question_i_d=#{questionID}, title=#{title}, summary=#{summary}, cover=#{cover}, `type`=#{type}, solution_num=#{solutionNum}, image_description=#{imageDescription}, `date`=#{date}, statu=#{statu}, reason=#{reason}, account=#{account}, tag=#{tag},
WHERE question_i_d=#{questionID}
