mapper.select:
SELECT question_i_d,solution_i_d,resolver,content,comment,favor,opposition,cite_solution,`date`,statu,reason
FROM `solution`
WHERE 1=1
AND question_i_d=#{questionID}
AND solution_i_d=#{solutionID}
AND resolver=#{resolver}
AND content=#{content}
AND comment=#{comment}
AND favor=#{favor}
AND opposition=#{opposition}
AND cite_solution=#{citeSolution}
AND `date`=#{date}
AND statu=#{statu}
AND reason=#{reason}

mapper.delete:
DELETE FROM `solution`
WHERE 1=1
AND question_i_d=#{questionID}
AND solution_i_d=#{solutionID}
AND resolver=#{resolver}
AND content=#{content}
AND comment=#{comment}
AND favor=#{favor}
AND opposition=#{opposition}
AND cite_solution=#{citeSolution}
AND `date`=#{date}
AND statu=#{statu}
AND reason=#{reason}

mapper.update:
UPDATE `solution`
solution_i_d=#{solutionID}, question_i_d=#{questionID}, resolver=#{resolver}, content=#{content}, comment=#{comment}, favor=#{favor}, opposition=#{opposition}, cite_solution=#{citeSolution}, `date`=#{date}, statu=#{statu}, reason=#{reason},
WHERE solution_i_d=#{solutionID}
